  // GLOBAL DATA STORE
  var engineDatabase = [];

  /* DATA EXTRACTION LOOP */
  <b:loop values='data:posts' var='post'>
    engineDatabase.push({
      title: "<data:post.title/>",
      url: "<data:post.url/>",
      snippet: "<data:post.snippet/>",
      author: "<data:post.author/>",
      timestamp: "<data:post.timestamp/>"
    });
  </b:loop>

  // UI ELEMENT REFERENCES
  var searchInput = document.getElementById('searchInput');
  var searchBoxUI = document.getElementById('searchBoxUI');
  var mainTerminal = document.getElementById('mainTerminal');
  var resultsMain = document.getElementById('results-main');
  var resultsContainer = document.getElementById('results-container');
  var searchStats = document.getElementById('search-stats');
  var loader = document.getElementById('search-loader');

  /* CORE SEARCH EXECUTION */
  function executeSearch() {
    var query = searchInput.value.toLowerCase().trim();
    if (query === "") {
      searchInput.focus();
      return;
    }
    document.body.classList.add('searching');
    loader.style.display = 'block';
    resultsContainer.innerHTML = '';
    searchStats.innerHTML = 'Searching for "' + query + '"...';

    setTimeout(function() {
      performMatch(query);
    }, 600);
  }

  /* PERFORM MATCH */
  function performMatch(query) {
    var startTime = performance.now();
    var matches = [];

    engineDatabase.forEach(function(post) {
      if (post.title.toLowerCase().indexOf(query) !== -1 || 
          post.snippet.toLowerCase().indexOf(query) !== -1) {
        matches.push(post);
      }
    });

    var endTime = performance.now();
    var searchTime = ((endTime - startTime) / 1000).toFixed(2);
    loader.style.display = 'none';

    if (matches.length > 0) {
      searchStats.innerHTML = 'About ' + matches.length + ' results (' + searchTime + ' seconds)';
      matches.forEach(function(item, index) {
        var delay = index * 0.1;
        resultsContainer.innerHTML += `
          <div class="result-item" style="animation-delay: ${delay}s">
            <span class="result-url">${item.url}</span>
            <a class="result-link" href="${item.url}">${item.title}</a>
            <p class="result-snippet">${item.snippet}</p>
            <div style="margin-top:10px; font-size:12px; color:#70757a;">
              Published by ${item.author} â€¢ ${item.timestamp}
            </div>
          </div>
        `;
      });
    } else {
      renderNoResults(query);
    }
  }

  /* NO RESULTS UI */
  function renderNoResults(query) {
    resultsContainer.innerHTML = `
      <div style="padding: 20px; color: #202124;">
        <p>Your search - <b>${query}</b> - did not match any documents.</p>
        <p style="margin-top:15px;">Suggestions:</p>
        <ul style="margin-left:40px; margin-top:10px; line-height:1.8;">
          <li>Make sure that all words are spelled correctly.</li>
          <li>Try different keywords.</li>
          <li>Try more general keywords.</li>
        </ul>
      </div>
    `;
  }

  /* I'M FEELING LUCKY */
  function luckySearch() {
    var query = searchInput.value.toLowerCase().trim();
    if (query === "") {
      if (engineDatabase.length > 0) {
        window.location.href = engineDatabase[0].url;
      }
      return;
    }

    var match = null;
    // The '<' below is now safe because of the CDATA wrapper
    for (var i = 0; i < engineDatabase.length; i++) {
      if (engineDatabase[i].title.toLowerCase().indexOf(query) !== -1) {
        match = engineDatabase[i];
        break; 
      }
    }

    if (match) {
      window.location.href = match.url;
    } else {
      executeSearch();
    }
  }

            if (match) {
              window.location.href = match.url;
            } else {
              executeSearch(); // Fallback to standard search if no direct match
            }
          }

          /* RESET SEARCH 
             Returns the UI to the centered 'Google Home' look
          */
          function resetSearch() {
            document.body.classList.remove('searching');
            resultsMain.style.display = 'none';
            resultsContainer.innerHTML = '';
            searchStats.innerHTML = '';
            searchInput.value = '';
            loader.style.display = 'none';

            // Re-center the layout with a smooth transition
            mainTerminal.style.display = 'flex';
            window.scrollTo({ top: 0, behavior: 'smooth' });
          }

          /* KEYBOARD ACCESSIBILITY 
             Ensure "Enter" works for both search and lucky
          */
          searchInput.addEventListener("keypress", function(event) {
            if (event.key === "Enter") {
              event.preventDefault();
              executeSearch();
            }
          });
          /* VOICE SEARCH SIMULATION
             Adds a high-tech animation when the mic icon is clicked
          */
          function simulateVoiceSearch() {
            searchInput.placeholder = "Listening...";
            searchBoxUI.classList.add('search-terminal-active');

            // In a real IT project, you would use the Web Speech API here
            setTimeout(function() {
              searchInput.placeholder = "Search the web or type a URL";
              searchBoxUI.classList.remove('search-terminal-active');
              alert("Voice search is active. Please speak now (Simulation).");
            }, 2000);
          }

          // INITIALIZATION
          window.onload = function() {
            console.log("BharatEngine Loaded: " + engineDatabase.length + " posts indexed.");
            // Add focus effect to search bar on load
            searchInput.focus();
          };

