/* FUNCTION: Toggle Dark Mode
   Applies dark theme classes and updates UI elements
*/
function toggleDarkMode() {
  const isDark = document.body.classList.toggle('dark-theme');
  document.getElementById('mode-text').innerText = isDark ? 'Light Mode' : 'Dark Mode';

  // Update persistent style via CSS Variables
  if(isDark) {
    document.documentElement.style.setProperty('--glass', 'rgba(32, 33, 36, 0.95)');
    document.documentElement.style.setProperty('--text-primary', '#e8eaed');
  } else {
    document.documentElement.style.setProperty('--glass', 'rgba(255, 255, 255, 0.95)');
    document.documentElement.style.setProperty('--text-primary', '#202124');
  }
}

/* FUNCTION: Background Customization
   Allows users to upload their own gallery image as a theme
*/
function triggerBgUpload() {
  document.getElementById('bgInput').click();
}

function updateBackground(input) {
  if (input.files && input.files[0]) {
    var reader = new FileReader();
    reader.onload = function(e) {
      document.getElementById('bgOverlay').style.backgroundImage = "url('" + e.target.result + "')";
      document.getElementById('bgOverlay').style.filter = "brightness(0.6)"; // Dim image for better text contrast
    };
    reader.readAsDataURL(input.files[0]);
  }
}

