/* FUNCTION: Filter Results
   Allows the user to narrow down posts by category (Projects/Internships)
*/
function filterResults(category) {
  // Update UI Tabs
  const tabs = document.querySelectorAll('.tab-item');
  tabs.forEach(tab => {
    tab.style.color = '#70757a';
    tab.style.borderBottom = 'none';
    if(tab.innerText.toLowerCase() === category) {
       tab.style.color = '#4285f4';
       tab.style.borderBottom = '3px solid #4285f4';
    }
  });

  const query = document.getElementById('searchInput').value.toLowerCase();

  // Filter the existing results based on keywords
  const filtered = engineDatabase.filter(post => {
    const matchesQuery = post.title.toLowerCase().indexOf(query) !== -1;
    if(category === 'all') return matchesQuery;
    return matchesQuery && post.snippet.toLowerCase().indexOf(category.slice(0, -1)) !== -1;
  });

  renderFilteredCards(filtered);
}

function renderFilteredCards(matches) {
  const container = document.getElementById('results-container');
  container.innerHTML = '';
  matches.forEach((item, index) => {
    container.innerHTML += `
      <div class="result-item" style="animation-delay: ${index * 0.05}s; opacity: 1; transform: translateY(0);">
        <span class="result-url">${item.url}</span>
        <a class="result-link" href="${item.url}">${item.title}</a>
        <p class="result-snippet">${item.snippet}</p>
      </div>
    `;
  });
}

// Modify executeSearch to show tabs
const originalExecuteSearch = executeSearch;
executeSearch = function() {
  originalExecuteSearch();
  document.getElementById('search-tabs').style.display = 'block';
}

