/* PRE-DEFINED SUGGESTIONS 
   Tailored to your background in Electronics, Data Science, and your projects.
*/
const smartKeywords = [
  "Data Science Internships",
  "Electronics and Telecommunication Projects",
  "Jay Bhavani Caterers Promo",
  "Shrinath Rasvanti Gruh Videos",
  "Pune IT Domain Vacancies",
  "B.Tech Portfolio",
  "Python for Data Analysis"
];

// Add the box to the UI dynamically
const sBox = document.createElement('div');
sBox.className = 'suggestions-box';
sBox.id = 'suggestionsBox';
document.querySelector('.search-field-container').parentNode.appendChild(sBox);

searchInput.addEventListener('input', function() {
  const val = this.value.toLowerCase();
  sBox.innerHTML = '';

  if (val.length < 1) {
    sBox.style.display = 'none';
    searchBoxUI.style.borderRadius = '24px';
    return;
  }

  const matches = smartKeywords.filter(k => k.toLowerCase().includes(val));

  if (matches.length > 0) {
    sBox.style.display = 'block';
    searchBoxUI.style.borderRadius = '24px 24px 0 0';

    matches.forEach(match => {
      const item = document.createElement('div');
      item.className = 'suggestion-item';
      item.innerHTML = `
        <svg class="suggestion-icon" width="16" height="16" viewBox="0 0 24 24"><path d="M15.5 14h-.79l-.28-.27A6.471 6.471 0 0 0 16 9.5 6.5 6.5 0 1 0 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z"/></svg>
        <span>${match}</span>
      `;
      item.onclick = () => {
        searchInput.value = match;
        sBox.style.display = 'none';
        executeSearch();
      };
      sBox.appendChild(item);
    });
  } else {
    sBox.style.display = 'none';
  }
});

// Close suggestions when clicking outside
document.addEventListener('click', (e) => {
  if (!document.querySelector('.search-bar-outer').contains(e.target)) {
    sBox.style.display = 'none';
    searchBoxUI.style.borderRadius = '24px';
  }
});

