function showMediaCarousel(query) {
  const row = document.createElement('div');
  row.className = 'media-results-row';
  row.id = 'mediaCarousel';

  // Check if query matches your business projects
  const isCatering = query.includes("caterer") || query.includes("bhavani");
  const isRasvanti = query.includes("rasvanti") || query.includes("shrinath");

  if (isCatering || isRasvanti) {
    row.style.display = 'block';
    let html = '<h3 style="font-size:18px; margin-bottom:15px; color:#202124;">Related Videos</h3>';

    mediaProjects.forEach(proj => {
      html += `
        <div class="media-card" onclick="window.location.href='${proj.link}'">
          <div class="media-thumb" style="background-color: ${proj.thumbColor}">
            <svg class="play-icon" width="48" height="48" viewBox="0 0 24 24"><path d="M8 5v14l11-7z"/></svg>
          </div>
          <div class="media-info">
            <div class="media-title">${proj.title}</div>
            <div style="font-size:12px; color:#70757a; margin-top:5px;">YouTube â€¢ BharatVideo</div>
          </div>
        </div>
      `;
    });
    row.innerHTML = html;
    document.getElementById('results-container').prepend(row);
  }
}

