const MAX_HISTORY = 6;

function saveToHistory(query) {
  let history = JSON.parse(localStorage.getItem('netquest_history') || '[]');
  // Remove duplicate if it exists
  history = history.filter(item => item !== query);
  // Add to front
  history.unshift(query);
  // Trim to max limit
  if (history.length > MAX_HISTORY) history.pop();

  localStorage.setItem('netquest_history', JSON.stringify(history));
  displayHistory();
}

function displayHistory() {
  const history = JSON.parse(localStorage.getItem('netquest_history') || '[]');
  const list = document.getElementById('historyList');
  const container = document.getElementById('historyBox');

  if (history.length > 0 && !document.body.classList.contains('searching')) {
    container.style.display = 'block';
    list.innerHTML = '';
    history.forEach(term => {
      const item = document.createElement('div');
      item.className = 'history-item';
      item.innerHTML = `<svg width="12" height="12" viewBox="0 0 24 24" fill="#9aa0a6"><path d="M13 3c-4.97 0-9 4.03-9 9H1l3.89 3.89.07.14L9 12H6c0-3.87 3.13-7 7-7s7 3.13 7 7-3.13 7-7 7c-1.93 0-3.68-.79-4.94-2.06l-1.42 1.42C8.27 19.99 10.51 21 13 21c4.97 0 9-4.03 9-9s-4.03-9-9-9zm-1 5v5l4.28 2.54.72-1.21-3.5-2.08V8H12z"/></svg> ${term}`;
      item.onclick = () => {
        searchInput.value = term;
        executeSearch();
      };
      list.appendChild(item);
    });
  } else {
    container.style.display = 'none';
  }
}

function clearHistory() {
  localStorage.removeItem('netquest_history');
  displayHistory();
}

// Update executeSearch to save history
const originalSearchWithHistory = executeSearch;
executeSearch = function() {
  const query = searchInput.value.trim();
  if (query !== "") saveToHistory(query);
  originalSearchWithHistory();
  document.getElementById('historyBox').style.display = 'none';
};

// Initialize history on load
window.addEventListener('load', displayHistory);

