const previewAssets = {
  "data science": "https://images.unsplash.com/photo-1551288049-bbda4e23215c?w=400",
  "electronics": "https://images.unsplash.com/photo-1517077304055-6e89abf0980d?w=400",
  "internship": "https://images.unsplash.com/photo-1523240795612-9a054b0db644?w=400",
  "b.tech": "https://images.unsplash.com/photo-1523050335456-adaba1710640?w=400"
};

function attachHoverPreviews() {
  const results = document.querySelectorAll('.result-item');
  const previewBox = document.getElementById('imagePreview');
  const previewImg = document.getElementById('previewImg');
  const previewTitle = document.getElementById('previewTitle');

  results.forEach(item => {
    item.addEventListener('mousemove', function(e) {
      const title = this.querySelector('.result-link').innerText.toLowerCase();
      let matchedKey = Object.keys(previewAssets).find(key => title.includes(key));

      if (matchedKey) {
        previewImg.src = previewAssets[matchedKey];
        previewTitle.innerText = this.querySelector('.result-link').innerText;

        previewBox.style.display = 'block';
        previewBox.style.left = (e.clientX + 20) + 'px';
        previewBox.style.top = (e.clientY + 20) + 'px';
      }
    });

    item.addEventListener('mouseleave', function() {
      previewBox.style.display = 'none';
    });
  });
}

// Hook into rendering
const originalRenderWithPreviews = renderFilteredCards;
renderFilteredCards = function(matches) {
  originalRenderWithPreviews(matches);
  attachHoverPreviews();
};

