/* FUNCTION: Toggle SafeSearch
   Filters out sensitive keywords from results if active
*/
function toggleSafeSearch() {
  const isActive = document.getElementById('safeSearchToggle').checked;
  const msg = isActive ? "SafeSearch is now ON" : "SafeSearch is now OFF";

  // Show a brief toast notification
  const toast = document.createElement('div');
  toast.style = "position:fixed; bottom:100px; left:50%; transform:translateX(-50%); background:#323232; color:#fff; padding:10px 20px; border-radius:4px; font-size:14px; z-index:5000;";
  toast.innerText = msg;
  document.body.appendChild(toast);

  setTimeout(() => toast.remove(), 2000);

  // In a real IT project, this state would filter the 'engineDatabase'
  localStorage.setItem('netquest_safesearch', isActive);
}

